.ocu-switch{
  &.form-group{
    @apply flex flex-wrap;
    input[type=checkbox]{
      /* main element = track */
      @apply appearance-none;
      @apply flex items-center relative;
      @apply p-0 mr-2 mb-0 ;
      @apply h-6 w-10 rounded-[0.7rem] bg-light;
      @apply transition-[background-color_0.2s,transform_0.2s] ;

      /* hover state  */
      @apply hover:cursor-pointer;

      /* After = handle */
      @apply after:content-[''];
      @apply after:absolute after:z-[2];
      @apply after:bg-neutral-100;
      @apply after:h-[1.1rem] after:w-[1.1rem];
      @apply after:ml-[0.1875rem] after:mr-[0.1875rem];
      @apply after:left-0;
      @apply after:rounded-full after:border-none;
      @apply after:shadow-[0_0px_3px_0_rgb(0_0_0_/_7%),_0_2px_2px_0_rgb(0_0_0_/_4%)];
      @apply after:transition-all;

      /* Checked state of track */
      @apply checked:bg-accent;

      /* checked state of handle */
      @apply checked:after:left-[calc(100%-0.1875rem-0.1875rem)] checked:after:transform checked:after:-translate-x-full;

      /* focus state */
      @apply focus:outline-none focus:after:z-[1] focus:after:block;
      @apply checked:focus:border-accent checked:focus:bg-accent;

      /* Dark Mode */
      @apply dark:bg-neutral-600 dark:after:bg-neutral-400 dark:checked:bg-accent dark:checked:after:bg-accent;
    }
    &.invalid{
      input[type=checkbox]{
        @apply bg-none border-none; /* reset ocufield global error styles */
        @apply bg-warning bg-opacity-50  checked:bg-warning checked:bg-opacity-100;
        @apply checked:focus:border-warning checked:focus:bg-warning ;
        @apply dark:checked:bg-warning dark:checked:after:bg-warning ;
      }
    }
  }
}
